INSERT INTO `configuration` VALUES(18, 'Basis of Product Tax', 'STORE_PRODUCT_TAX_BASIS', 'Shipping', 'On what basis is Product Tax calculated. Options are<br />Shipping - Based on customers Shipping Address<br />Billing Based on customers Billing address<br />Store - Based on Store address if Billing/Shipping Zone equals Store zone', 1, 21, NULL, '2015-07-10 09:53:18', NULL, 'zen_cfg_select_option(array(''Shipping'', ''Billing'', ''Store''), ');
