INSERT INTO `configuration` VALUES(22, 'PA-DSS Admin Session Timeout Enforced?', 'PADSS_ADMIN_SESSION_TIMEOUT_ENFORCED', '1', 'PA-DSS Compliance requires that any Admin login sessions expire after 15 minutes of inactivity. <strong>Disabling this makes your site NON-COMPLIANT with PA-DSS rules, thus invalidating any certification.</strong>', 1, 30, '2015-07-10 09:53:18', '2015-07-10 09:53:18', NULL, 'zen_cfg_select_drop_down(array(array(''id''=>''0'', ''text''=>''Non-Compliant''), array(''id''=>''1'', ''text''=>''On'')),');
INSERT INTO `configuration` VALUES(23, 'PA-DSS Strong Password Rules Enforced?', 'PADSS_PWD_EXPIRY_ENFORCED', '1', 'PA-DSS Compliance requires that admin passwords must be changed after 90 days and cannot re-use the last 4 passwords. <strong>Disabling this makes your site NON-COMPLIANT with PA-DSS rules, thus invalidating any certification.</strong>', 1, 30, '2015-07-10 09:53:18', '2015-07-10 09:53:18', NULL, 'zen_cfg_select_drop_down(array(array(''id''=>''0'', ''text''=>''Non-Compliant''), array(''id''=>''1'', ''text''=>''On'')),');
INSERT INTO `configuration` VALUES(24, 'PA-DSS Ajax Checkout?', 'PADSS_AJAX_CHECKOUT', '1', 'PA-DSS Compliance requires that for some inbuilt payment methods, that we use ajax to draw the checkout confirmation screen. While this will only happen if one of those payment methods is actually present, some people may want the traditional checkout flow <strong>Disabling this makes your site NON-COMPLIANT with PA-DSS rules, thus invalidating any certification.</strong>', 1, 30, '2015-07-10 09:53:18', '2015-07-10 09:53:18', NULL, 'zen_cfg_select_drop_down(array(array(''id''=>''0'', ''text''=>''Non-Compliant''), array(''id''=>''1'', ''text''=>''On'')),');
INSERT INTO `configuration` VALUES(25, 'Admin Session Time Out in Seconds', 'SESSION_TIMEOUT_ADMIN', '900', 'Enter the time in seconds.<br />Max allowed is 900 for PCI Compliance Reasons.<br /> Default=900<br />Example: 900= 15 min <br /><br />Note: Too few seconds can result in timeout issues when adding/editing products', 1, 40, NULL, '2015-07-10 09:53:18', NULL, NULL);
INSERT INTO `configuration` VALUES(26, 'Admin Set max_execution_time for processes', 'GLOBAL_SET_TIME_LIMIT', '60', 'Enter the time in seconds for how long the max_execution_time of processes should be. Default=60<br />Example: 60= 1 minute<br /><br />Note: Changing the time limit is only needed if you are having problems with the execution time of a process', 1, 42, NULL, '2015-07-10 09:53:18', NULL, NULL);
